import {fabric} from 'fabric';
import { Bounds } from './Bounds';
import { Editor } from './Editor';
import { ElementBuilder } from './ElementBuilder';

const presavedJson = localStorage.getItem('fabricjs-json');
const canvas = new fabric.Canvas('canvas');

const bounds = new Bounds(canvas);
const editor = new Editor(canvas, bounds);
const elementBuilder = new ElementBuilder(editor);

if (presavedJson) {
  console
  canvas.clear();
  canvas.loadFromJSON(presavedJson, () => {
    canvas.renderAll();
  });
} else {
  elementBuilder.addRect({
    top: 30,
    left: 30,
    width: 60,
    height: 70,
    fill: 'red',
    hasControls: true,
    outerBounds: true
  });

  elementBuilder.addText({
    text: 'test',
    editable: true,
    innerBounds: true,
    top: 50,
    left: 100
  })
}

function setupControls() {
    const toggleBounds = document.getElementById('toggle-bounds');
    if(toggleBounds) { 
      toggleBounds.onclick = function() {
        editor.toggleBoundingLayer();
      }
    }

    const spawnImage = document.getElementById('spawn-image');
    if(spawnImage) { 
      spawnImage.onclick = function() {
        elementBuilder.addImage({
          src: 'data:image/jpeg;base64,',
          left: 120,
          top: 120,
          width: 110,
          height: 120
        })
      }
    }

    const save = document.getElementById('save-canvas');
    if(save) { 
      save.onclick = function() {
        localStorage.setItem('fabricjs-json', JSON.stringify(canvas.toDatalessJSON()));
      }
    }
  }

  setupControls();
